mapper.select:
SELECT rownum, DIL_DOCNO, DIL_LOGC, MCI_LOGCNAME, DIL_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DIL_JEJO, DIL_INDate, DIL_INPRICE, DIL_QTY, INTOTAL, bizSTS, DIL_OUTSts, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DIL_INDate desc, DIL_LOGC ) AS rownum, DIL_DOCNO, DIL_LOGC, MCI_LOGCNAME, DIL_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DIL_JEJO, 23) as DIL_JEJO, CONVERT(CHAR(10), DIL_INDate, 23) as DIL_INDate, DIL_INPRICE, DIL_QTY, DIL_INPRICE * DIL_QTY as INTOTAL, case when DBI_TOPDOCNO IS NULL then '0' when not B_CNT = SUS_CNT then '1' when B_CNT = SUS_CNT then '2' else '0' end as bizSTS, DIL_OUTSts, COUNT(*) OVER() AS totalCnt FROM MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE, DT_IN_LOGC left outer join (SELECT DBI_TOPDOCNO, COUNT(DBI_DOCNO) as B_CNT, SUM(CASE WHEN DBI_BisSts = 'Y' then 1 else 0 end) AS SUS_CNT FROM DT_BONBU_IN group by DBI_TOPDOCNO) B on DIL_DOCNO = B.DBI_TOPDOCNO WHERE DIL_LOGC = MCI_LOGC AND MCI_BONBU = ' ' AND MCI_CENTER = ' ' AND DIL_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE
AND DIL_INDate <= #{EndDay}
AND DIL_INDate >= #{StartDay}
AND DIL_INDate between #{StartDay} AND #{EndDay}
AND DIL_LOGC = #{LOGC}
AND DIL_MODEL like '%${MNAME}%'
AND DBI_TOPDOCNO IS NULL
AND not B_CNT = SUS_CNT
AND B_CNT = SUS_CNT
)A WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT rownum, DIL_DOCNO, DIL_LOGC, MCI_LOGCNAME, DIL_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DIL_JEJO, DIL_INDate, DIL_OUTDATE, DIL_INPRICE, DIL_QTY, INTOTAL, bizSTS, DIL_OUTSts, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DIL_INDate desc, DIL_LOGC ) AS rownum, DIL_DOCNO, DIL_LOGC, MCI_LOGCNAME, DIL_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DIL_JEJO, 23) as DIL_JEJO, CONVERT(CHAR(10), DIL_INDate, 23) as DIL_INDate, CONVERT(CHAR(10), DIL_OUTDATE, 23) as DIL_OUTDATE, DIL_INPRICE, DIL_QTY, DIL_INPRICE * DIL_QTY as INTOTAL, case when DBI_TOPDOCNO = NULL then '0' when B_CNT = 0 then '0' when not B_CNT = SUS_CNT then '1' when B_CNT = SUS_CNT then '2' else '0' end as bizSTS, DIL_OUTSts, COUNT(*) OVER() AS totalCnt FROM MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE, DT_IN_LOGC left outer join (SELECT DBI_TOPDOCNO, COUNT(DBI_DOCNO) as B_CNT, SUM(CASE WHEN DBI_BisSts = 'Y' then 1 else 0 end) AS SUS_CNT FROM DT_BONBU_IN group by DBI_TOPDOCNO) B on DIL_DOCNO = B.DBI_TOPDOCNO WHERE DIL_OUTSts = 'Y' AND DIL_LOGC = MCI_LOGC AND MCI_BONBU = ' ' AND MCI_CENTER = ' ' AND DIL_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE
AND DIL_INDate <= #{InEndDay}
AND DIL_INDate >= #{InStartDay}
AND DIL_INDate between #{InStartDay} AND #{InEndDay}
AND DIL_OUTDATE <= #{OutEndDay}
AND DIL_OUTDATE >= #{OutStartDay}
AND DIL_OUTDATE between #{OutStartDay} AND #{OutEndDay}
AND DIL_LOGC = #{LOGC}
AND DIL_MODEL like '%${MNAME}%'
)A WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT MCI_CODE, MCI_BonbuNAME, MCI_CenterName, ISNULL(MMJ_JEGO,'0') as MMJ_JEGO, ISNULL(DMJ_JEGO,'0') as DMJ_JEGO, DBI_DOCNO, ISNULL(DBI_QTY,'0') as DBI_QTY, DBI_BisSts
  FROM MF_CENTER_INFO
       left outer join DT_BONBU_IN on MCI_CODE = DBI_CENTER and DBI_TOPDOCNO = #{DOCNO}
       left outer join MF_MODEL_JEGO on MCI_CODE = MMJ_CENTER and MMJ_MODEL = #{MODEL}
       left outer join DT_MODEL_JEGO on MCI_CODE = DMJ_CENTER and DMJ_MODEL = #{MODEL}
 WHERE MCI_LOGC = #{LOGC} 
   AND NOT MCI_Bonbu = ' ' 
   AND NOT MCI_Center = ' '
   AND (DBI_CENTER is not null or DBI_CENTER is null and MCI_BizSts = 'Y')

SELECT COUNT(DBI_DOCNO) 
  FROM DT_BONBU_IN
 WHERE DBI_TOPDOCNO = #{value}

mapper.update:
update DT_PRICE_IN
   set DPI_OUTQTY = B.DPI_OUTQTY + #{JEGO}
  FROM DT_PRICE_IN B
 WHERE DPI_DOCNO = #{DOCNO}
   AND DPI_CENTER = #{CENTER}
   AND DPI_MODEL = #{MODEL}
   AND DPI_LOGCNO = #{LOGCNO}
   AND DPI_DOCNO = B.DPI_DOCNO
   AND DPI_CENTER = B.DPI_CENTER
   AND DPI_MODEL = B.DPI_MODEL
   AND DPI_LOGCNO = B.DPI_LOGCNO

   AND DPI_OUTQTY >= (#{JEGO}*(-1))

		update DT_PRICE_OUT
		   set DPO_QTY = B.DPO_QTY + #{JEGO}
		  FROM DT_PRICE_OUT B, DT_PRICE_IN
         WHERE DPO_INDOCNO = #{DOCNO}
           AND DPO_DOCNO = #{OUTDOCNO}
           AND DPO_CENTER = #{CENTER}
           AND DPO_MODEL = #{MODEL}
           AND DPO_LOGCNO = #{LOGCNO}
           AND DPO_INDOCNO = B.DPO_INDOCNO
           AND DPO_DOCNO = B.DPO_DOCNO
           AND DPO_CENTER = B.DPO_CENTER
           AND DPO_MODEL = B.DPO_MODEL
           AND DPO_LOGCNO = B.DPO_LOGCNO
           AND B.DPO_INDOCNO = DPI_DOCNO
           AND B.DPO_CENTER = DPI_CENTER
           AND B.DPO_MODEL = DPI_MODEL
           AND B.DPO_LOGCNO = DPI_LOGCNO
           AND (B.DPO_QTY + #{JEGO}) between 1 and DPI_QTY

mapper.delete:
	delete from DT_PRICE_OUT
     where DPO_INDOCNO = #{DOCNO}
       AND DPO_DOCNO = #{OUTDOCNO}
       AND DPO_CENTER = #{CENTER}
       AND DPO_MODEL = #{MODEL}
       AND DPO_LOGCNO = #{LOGCNO}

delete from DT_BONBU_IN
 where DBI_TOPDOCNO = #{value}
   AND DBI_BisSts = 'N'

mapper.select:
SELECT rownum, DBO_DOCNO, DBO_CENTER, MCI_BonbuNAME, MCI_CenterName, DBO_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DBO_DATE, DBO_USER, DBO_USERNAME, DBO_QTY, DBM_Sts, DBM_QTY, DBO_BisSts, DBO_CFMDATE, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DBO_DATE desc, DBO_CENTER ) AS rownum, DBO_DOCNO, DBO_CENTER, MCI_BonbuNAME, MCI_CenterName, DBO_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DBO_DATE, 23) as DBO_DATE, DBO_USER, MUT_UserName as DBO_USERNAME, DBO_QTY, DBM_Sts, ISNULL(DBM_QTY,'0') as DBM_QTY, DBO_BisSts, CONVERT(CHAR(10), DBO_CFMDATE, 23) as DBO_CFMDATE, COUNT(*) OVER() AS totalCnt FROM MF_USER_TBL, MF_COMM_CODE, MF_MODEL_CODE, MF_CENTER_INFO, DT_BONBU_MOVE, DT_BONBU_ORDER WHERE DBO_DOCNO = DBM_DOCNO AND DBO_CENTER = MCI_CODE AND DBO_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND DBO_USER = MUT_UserID
AND DBO_DATE <= #{EndDay}
AND DBO_DATE >= #{StartDay}
AND DBO_DATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_BONBU = #{BONBU}
AND MCI_CENTER = #{CENTER}
AND MCI_CODE = #{CCODE}
AND DBO_MODEL like '%${MNAME}%'
)A WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT rownum, DBO_DOCNO, DBO_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP,
	   OUTBONBUNAME, OUTCENTERNAME, INBONBUNAME, INCENTERNAME, 
	   DBO_DATE, DBM_DATE, DBO_CFMDATE, DBM_Sts, DBO_BisSts, totalCnt
FROM
(
	SELECT ROW_NUMBER() OVER(ORDER BY DBM_Sts, DBO_BisSts, DBO_DATE DESC, DBM_DATE DESC, DBO_CFMDATE DESC) AS rownum, 
		   DBO_DOCNO, DBO_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, 
		   A.MCI_BonbuNAME AS OUTBONBUNAME, A.MCI_CenterName AS OUTCENTERNAME,
		   B.MCI_BonbuNAME AS INBONBUNAME, B.MCI_CenterName AS INCENTERNAME,
		   CONVERT(CHAR(10), DBO_DATE, 23) as DBO_DATE, 
	       CONVERT(CHAR(10), DBM_DATE, 23) as DBM_DATE, 
	       CONVERT(CHAR(10), DBO_CFMDATE, 23) as DBO_CFMDATE,
	       DBM_Sts, DBO_BisSts,
	       COUNT(*) OVER() AS totalCnt
	  FROM DT_BONBU_MOVE
      	   left outer join MF_CENTER_INFO A on DBM_CENTER = A.MCI_CODE,
      	   DT_BONBU_ORDER, MF_CENTER_INFO B, MF_MODEL_CODE, MF_COMM_CODE
	 WHERE DBM_DOCNO = DBO_DOCNO
	   AND DBO_CENTER = B.MCI_CODE
	   AND DBO_MODEL = MMC_CODE
	   AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE

	 	AND 
       	(
           	DBM_DATE is null or

       		DBM_DATE <= #{OutEndDay}


       		DBM_DATE >= #{OutStartDay}


       		DBM_DATE between #{OutStartDay} AND #{OutEndDay}

       	)


	 	AND 
       	(
           	DBO_CFMDATE is null or

       		DBO_CFMDATE <= #{InEndDay}


       		DBO_CFMDATE >= #{InStartDay}


       		DBO_CFMDATE between #{InStartDay} AND #{InEndDay}

       	)


	   	AND 
       	(
           	B.MCI_LOGC = #{INLOGC}

       		AND B.MCI_BONBU = #{INBONBU}


       		AND B.MCI_CENTER = #{INCENTER}

       	)


	   	AND 
       	(
           	DBM_CENTER is null or A.MCI_LOGC = #{LOGC}

       		AND A.MCI_BONBU = #{BONBU}


       		AND A.MCI_CENTER = #{CENTER}

       	) 


        AND DBO_MODEL like '%${MNAME}%'

) A
WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT rownum, DBO_DOCNO, DBO_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP,
	   OUTBONBUNAME, OUTCENTERNAME, INBONBUNAME, INCENTERNAME, 
	   DBO_DATE, DBM_DATE, DBO_CFMDATE, DBM_Sts, DBO_BisSts, totalCnt
FROM
(
	SELECT ROW_NUMBER() OVER(ORDER BY DBM_Sts, DBO_BisSts, DBO_DATE DESC, DBM_DATE DESC, DBO_CFMDATE DESC) AS rownum, 
		   DBO_DOCNO, DBO_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, 
		   A.MCI_BonbuNAME AS OUTBONBUNAME, A.MCI_CenterName AS OUTCENTERNAME,
		   B.MCI_BonbuNAME AS INBONBUNAME, B.MCI_CenterName AS INCENTERNAME,
		   CONVERT(CHAR(10), DBO_DATE, 23) as DBO_DATE, 
	       CONVERT(CHAR(10), DBM_DATE, 23) as DBM_DATE, 
	       CONVERT(CHAR(10), DBO_CFMDATE, 23) as DBO_CFMDATE,
	       DBM_Sts, DBO_BisSts,
	       COUNT(*) OVER() AS totalCnt
	  FROM DT_BONBU_MOVE
      	   left outer join MF_CENTER_INFO A on DBM_CENTER = A.MCI_CODE,
      	   DT_BONBU_ORDER
      	   left outer join MF_CENTER_INFO B on DBO_CENTER = B.MCI_CODE
      	   left outer join MF_MODEL_CODE on DBO_MODEL = MMC_CODE
	       left outer join MF_COMM_CODE on MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE
	 WHERE DBM_DOCNO = DBO_DOCNO

	 	AND 
       	(
           	DBM_DATE is null or

       		DBM_DATE <= #{OutEndDay}


       		DBM_DATE >= #{OutStartDay}


       		DBM_DATE between #{OutStartDay} AND #{OutEndDay}

       	)


	 	AND 
       	(
           	DBO_CFMDATE is null or

       		DBO_CFMDATE <= #{InEndDay}


       		DBO_CFMDATE >= #{InStartDay}


       		DBO_CFMDATE between #{InStartDay} AND #{InEndDay}

       	)



        AND B.MCI_LOGC = #{INLOGC}


       	AND B.MCI_BONBU = #{INBONBU}


       	AND B.MCI_CENTER = #{INCENTER}


        AND A.MCI_LOGC = #{LOGC}


        AND A.MCI_BONBU = #{BONBU}


       	AND A.MCI_CENTER = #{CENTER}



        AND (
        	B.MCI_LOGC = #{INLOGC} 
        	AND B.MCI_BONBU = #{INBONBU}

        	AND B.MCI_CENTER = #{INCENTER} 

        	OR 
        	A.MCI_LOGC = #{INLOGC} 
        	AND A.MCI_BONBU = #{INBONBU} 

        	AND A.MCI_CENTER = #{INCENTER} 

        	)


        AND DBO_MODEL like '%${MNAME}%'

) A
WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

choose.otherwise:
	SELECT DMJ_JEGO, DMJ_JEGO as OUTJEGO
	  FROM DT_MODEL_JEGO
     WHERE DMJ_MODEL = #{MODEL} 
	   and DMJ_CENTER = #{CENTER}

