   set DMJ_JEGO = B.DMJ_JEGO + (#{JEGO}*(-1))
  FROM DT_MODEL_JEGO B
 WHERE DMJ_MODEL = B.DMJ_MODEL
   AND DMJ_CENTER = B.DMJ_CENTER
   AND DMJ_MODEL = #{MODEL}
   AND DMJ_CENTER = #{CENTER}
   AND DMJ_JEGO >= #{JEGO}

mapper.select:
select rownum, INTYPE, DOCNO, INLOGC, INBONBU, INCENTER, INBONBU_NAME, INCENTER_NAME, OUT_BONBU, MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, INDATE, INQTY, OUTDATE, OUTQTY, DIC_DATE, CFMsts, totalCnt FROM ( select ROW_NUMBER() OVER(ORDER BY CFMsts, OUTDATE DESC, DOCNO) AS rownum, DOCNO, INTYPE, INLOGC, INBONBU, INCENTER, INBONBU_NAME, INCENTER_NAME, OUT_BONBU, MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, INDATE, INQTY, OUTDATE, OUTQTY, DIC_DATE, CFMsts, COUNT(*) OVER() AS totalCnt FROM ( select DBI_DOCNO as DOCNO, '0' AS INTYPE, B.MCI_LOGC as INLOGC, B.MCI_Bonbu as INBONBU, B.MCI_Center as INCENTER, B.MCI_BonbuNAME as INBONBU_NAME, B.MCI_CenterName as INCENTER_NAME, A.MCI_LOGCNAME as OUT_BONBU, DIL_MODEL as MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, '-' as INDATE, '0' as INQTY, CONVERT(CHAR(10), DIL_OUTDATE, 23) as OUTDATE, ISNULL(DBI_QTY,'0') as OUTQTY, CONVERT(CHAR(10), DIC_DATE, 23) as DIC_DATE, case when DIC_DOCNO is null then 'N' else 'Y' end as CFMsts FROM DT_IN_LOGC, MF_CENTER_INFO A, MF_CENTER_INFO B, MF_MODEL_CODE, MF_COMM_CODE, DT_BONBU_IN left outer join DT_IN_CFM on DBI_DOCNO = DIC_DOCNO WHERE DIL_DOCNO = DBI_TOPDOCNO AND DIL_LOGC = A.MCI_LOGC AND A.MCI_BONBU = ' ' AND A.MCI_CENTER = ' ' AND DIL_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND DBI_CENTER = B.MCI_CODE
AND DIL_OUTDATE <= #{EndDay}
AND DIL_OUTDATE >= #{StartDay}
AND DIL_OUTDATE between #{StartDay} AND #{EndDay}
UNION ALL select DBO_DOCNO AS DOCNO, '1' AS INTYPE, A.MCI_LOGC as INLOGC, A.MCI_Bonbu as INBONBU, A.MCI_Center as INCENTER, A.MCI_BonbuNAME as INBONBU_NAME, A.MCI_CenterName as INCENTER_NAME, B.MCI_BonbuNAME + ' ' + B.MCI_CenterName as OUT_BONBU, DBO_MODEL as MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DBO_DATE, 23) as INDATE, ISNULL(DBO_QTY,'0') as INQTY, CONVERT(CHAR(10), DBM_DATE, 23) as OUTDATE, ISNULL(DBM_QTY,'0') as OUTQTY, CONVERT(CHAR(10), DIC_DATE, 23) as DIC_DATE, case when DIC_DOCNO is null then 'N' else 'Y' end as CFMsts FROM DT_BONBU_MOVE, MF_CENTER_INFO A, MF_CENTER_INFO B, MF_MODEL_CODE, MF_COMM_CODE, DT_BONBU_ORDER left outer join DT_IN_CFM on DBO_DOCNO = DIC_DOCNO WHERE DBM_DOCNO = DBO_DOCNO AND DBM_Sts = 'Y' AND DBO_CENTER = A.MCI_CODE AND DBO_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND DBM_CENTER = B.MCI_CODE
AND DBM_DATE <= #{EndDay}
AND DBM_DATE >= #{StartDay}
AND DBM_DATE between #{StartDay} AND #{EndDay}
) SUB WHERE 1=1
AND INLOGC = #{LOGC}
AND INBONBU = #{BONBU}
AND INCENTER = #{CENTER}
AND MODEL like '%${MNAME}%'
AND INTYPE = #{INSTS}
AND CFMsts = #{CFMSTS}
) MAS WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

mapper.update:
	UPDATE DT_IN_CFM
	   SET DIC_CMNT = #{DIC_CMNT},
	       CMN_UPD_DATE = getdate(),
	       CMN_UPD_PROG = #{CMN_UPD_PROG},
       	   CMN_UPD_ID = #{CMN_UPD_ID}
     WHERE DIC_DOCNO = #{DIC_DOCNO}

mapper.select:
SELECT rownum, DOO_DOCNO, DOO_MODEL, DOO_CENTER, MCI_BonbuNAME, MCI_CenterName, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DOO_DATE, DOO_QTY, DOO_OUTCMNT, DOO_CMNT, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DOO_DATE desc, DOO_CENTER ) AS rownum, DOO_DOCNO, DOO_MODEL, DOO_CENTER, MCI_BonbuNAME, MCI_CenterName, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DOO_DATE, 23) as DOO_DATE, ISNULL(DOO_QTY,'0') as DOO_QTY, DOO_OUTCMNT, DOO_CMNT, COUNT(*) OVER() AS totalCnt FROM DT_Other_OUT, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE WHERE DOO_CENTER = MCI_CODE AND DOO_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE
AND DOO_DATE <= #{EndDay}
AND DOO_DATE >= #{StartDay}
AND DOO_DATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_BONBU = #{BONBU}
AND MCI_CENTER = #{CENTER}
AND DOO_MODEL like '%${MNAME}%'
)A WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

		SELECT DIB_IOTYPE, DIB_CENTER, DATEPART(mm, DIB_IODATE) as MONTH, DIB_RESN, SUM(DIB_QTY) as CNT
		  FROM DT_INOUT_BONBU, MF_CENTER_INFO
		 WHERE DIB_MODEL = #{MODEL}
		   AND DIB_IODATE BETWEEN #{StartDay} AND #{EndDay}

		   AND DIB_IOTYPE = '1'
		   AND (DIB_RESN = '1' or DIB_RESN = '2')


		   AND DIB_IOTYPE = '2'

		   AND DIB_CENTER = MCI_CODE


           AND MCI_LOGC = #{LOGC}
           AND not MCI_Bonbu = ' '
	       AND not MCI_Center = ' '


           AND MCI_LOGC = #{LOGC}
	       AND MCI_Bonbu = #{BONBU}
	       AND not MCI_Center = ' '


	       AND MCI_LOGC = #{LOGC}
	       AND MCI_Bonbu = #{BONBU}
	       AND MCI_Center = #{CENTER}


	     group by DIB_IOTYPE, DIB_CENTER, DATEPART(mm, DIB_IODATE), DIB_RESN
		 order by DIB_IOTYPE, DIB_CENTER, DATEPART(mm, DIB_IODATE), DIB_RESN

	SELECT DMJ_MODEL, DMJ_CENTER, DMJ_JEGO + ISNULL(SUM(case WHEN DIB_IOTYPE = '1' THEN DIB_QTY * (-1) else DIB_QTY end), 0) as JEGO
	  FROM MF_CENTER_INFO, DT_MODEL_JEGO
	  	   left outer join DT_INOUT_BONBU on DMJ_CENTER = DIB_CENTER and DMJ_MODEL = DIB_MODEL and DIB_IODATE > dateadd(year,-1,#{EndDay})
     WHERE DMJ_MODEL = #{MODEL}
       AND DMJ_CENTER = MCI_CODE


       AND MCI_LOGC = #{LOGC}
       AND not MCI_Bonbu = ' '
	   AND not MCI_Center = ' '


       AND MCI_LOGC = #{LOGC}
	   AND MCI_Bonbu = #{BONBU}
	   AND not MCI_Center = ' '


	   AND MCI_LOGC = #{LOGC}
	   AND MCI_Bonbu = #{BONBU}
	   AND MCI_Center = #{CENTER}


	 group by DMJ_MODEL, DMJ_CENTER, DMJ_JEGO

	SELECT LOGC, LOGCNAME, BONBU, BONBUNAME, CENTER, CENTERNAME, MCI_CODE, PJEGO, NOWJEGO, MMJ_JEGO,
	       PJEGO + SUM(CASE WHEN MMDATE = '1' THEN DIB_QTY else 0 end) M1JEGO,

	       PJEGO + SUM(CASE WHEN MMDATE <= '2' THEN DIB_QTY else 0 end) M2JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '3' THEN DIB_QTY else 0 end) M3JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '4' THEN DIB_QTY else 0 end) M4JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '5' THEN DIB_QTY else 0 end) M5JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '6' THEN DIB_QTY else 0 end) M6JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '7' THEN DIB_QTY else 0 end) M7JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '8' THEN DIB_QTY else 0 end) M8JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '9' THEN DIB_QTY else 0 end) M9JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '10' THEN DIB_QTY else 0 end) M10JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '11' THEN DIB_QTY else 0 end) M11JEGO,


	       PJEGO + SUM(CASE WHEN MMDATE <= '12' THEN DIB_QTY else 0 end) M12JEGO,

	       ''
	  FROM (
	  		SELECT MCI_LOGC as LOGC, MCI_LOGCNAME as LOGCNAME, MCI_Bonbu as BONBU, MCI_BonbuNAME as BONBUNAME, MCI_Center as CENTER, MCI_CenterName as CENTERNAME, MCI_CODE, 
	  		       ISNULL(DMJ_JEGO, 0) + ISNULL(P.JEGO, 0) as PJEGO, ISNULL(DMJ_JEGO,0) + ISNULL(N.JEGO,0) as NOWJEGO, ISNULL(MMJ_JEGO,0) as MMJ_JEGO, 
	  		       DIB_IODATE, DATEPART(mm, DIB_IODATE) MMDATE, CASE WHEN DIB_IOTYPE = '2' THEN DIB_QTY*(-1) else DIB_QTY end DIB_QTY
	  		  FROM MF_CENTER_INFO
	  		       left outer join DT_INOUT_BONBU A on DIB_MODEL = #{MODEL} AND MCI_CODE = A.DIB_CENTER  AND DIB_IODATE BETWEEN #{StartDay} AND #{EndDay}
	  		       left outer join MF_MODEL_JEGO on MMJ_CENTER = MCI_CODE and MMJ_MODEL = #{MODEL}
	       		   left outer join DT_MODEL_JEGO on DMJ_CENTER = MCI_CODE and DMJ_MODEL = #{MODEL}
	       		   left outer join 
	       		   (
		               SELECT DIB_CENTER, ISNULL(SUM(case WHEN DIB_IOTYPE = '1' THEN DIB_QTY * (-1) else DIB_QTY end), 0) as JEGO 
		                 FROM DT_INOUT_BONBU
		                WHERE DIB_MODEL = #{MODEL}
		                  and DIB_IODATE > #{pDay}
		                group by DIB_CENTER
	               ) P
		           on DMJ_CENTER = P.DIB_CENTER
		           left outer join 
		           (
		   	           SELECT DIB_CENTER, ISNULL(SUM(case WHEN DIB_IOTYPE = '1' THEN DIB_QTY * (-1) else DIB_QTY end), 0) as JEGO 
		                 FROM DT_INOUT_BONBU
		                WHERE DIB_MODEL = #{MODEL}
		                  and DIB_IODATE > #{NowDay}
		                group by DIB_CENTER
		           ) N
		           on DMJ_CENTER = N.DIB_CENTER


             WHERE MCI_LOGC = #{LOGC}
	           AND not MCI_Bonbu = ' '
	           AND not MCI_Center = ' '


     		 WHERE MCI_LOGC = #{LOGC}
	   		   AND MCI_Bonbu = #{BONBU}
	   		   AND not MCI_Center = ' '


	 		 WHERE MCI_LOGC = #{LOGC}
	   		   AND MCI_Bonbu = #{BONBU}
	   		   AND MCI_Center = #{CENTER}


	 	) MAIN
	 	GROUP BY LOGC, LOGCNAME, BONBU, BONBUNAME, CENTER, CENTERNAME, MCI_CODE, PJEGO, NOWJEGO, MMJ_JEGO
        ORDER BY LOGC, BONBU, CENTER

SELECT rownum, DOCNO, MODEL, IOTYPE, IODATE, QTY, RESNNAME, CCODE, MCI_LOGC, LOGCNAME, BONBUNAME, CENTERNAME, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY IODATE, CMN_MAK_DATE, MCI_LOGC, CCODE) AS rownum, DOCNO, MODEL, IOTYPE, IODATE, QTY, RESNNAME, CCODE, MCI_LOGC, LOGCNAME, BONBUNAME, CENTERNAME, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, COUNT(*) OVER() AS totalCnt FROM (
SELECT DIL_DOCNO as DOCNO, DIL_MODEL as MODEL, '1' as IOTYPE, CONVERT(CHAR(10), DIL_INDate, 23) as IODATE, ISNULL(DIL_QTY,'0') as QTY, '' as RESNNAME, '0' as CCODE, MCI_LOGC, MCI_LOGCNAME as LOGCNAME, '-' as BONBUNAME, '-' as CENTERNAME, MMC_LGROUP, A.MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DT_IN_LOGC.CMN_UPD_DATE AS CMN_MAK_DATE FROM DT_IN_LOGC, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE A WHERE DIL_LOGC = MCI_LOGC AND MCI_Bonbu = ' ' AND MCI_Center = ' ' AND DIL_MODEL = MMC_CODE AND A.MCC_L_CODE = '200' AND A.MCC_M_CODE = '201' AND MMC_LGROUP = A.MCC_S_CODE
AND DIL_INDate <= #{EndDay}
AND DIL_INDate >= #{StartDay}
AND DIL_INDate between #{StartDay} AND #{EndDay}
AND DIL_LOGC = #{LOGC}
AND DIL_MODEL like '%${MNAME}%'
UNION ALL
SELECT DIB_DOCNO as DOCNO, DIB_MODEL as MODEL, DIB_IOTYPE as IOTYPE, CONVERT(CHAR(10), DIB_IODATE, 23) as IODATE, ISNULL(DIB_QTY,'0') as QTY, case when DIB_IOTYPE = '1' then B.MCC_S_NAME ELSE C.MCC_S_NAME end as RESNNAME, MCI_CODE as CCODE, MCI_LOGC, MCI_LOGCNAME as LOGCNAME, MCI_BonbuNAME as BONBUNAME, MCI_CenterName as CENTERNAME, MMC_LGROUP, A.MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DT_INOUT_BONBU.CMN_MAK_DATE AS CMN_MAK_DATE FROM DT_INOUT_BONBU LEFT OUTER JOIN MF_COMM_CODE B ON DIB_IOTYPE = '1' AND B.MCC_L_CODE = '100' AND B.MCC_M_CODE = '100' AND DIB_RESN = B.MCC_S_CODE LEFT OUTER JOIN MF_COMM_CODE C ON DIB_IOTYPE = '2' AND C.MCC_L_CODE = '100' AND C.MCC_M_CODE = '101' AND DIB_RESN = C.MCC_S_CODE, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE A WHERE DIB_CENTER = MCI_CODE AND DIB_MODEL = MMC_CODE AND A.MCC_L_CODE = '200' AND A.MCC_M_CODE = '201' AND MMC_LGROUP = A.MCC_S_CODE
AND DIB_IODATE <= #{EndDay}
AND DIB_IODATE >= #{StartDay}
AND DIB_IODATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_BONBU = #{BONBU}
AND MCI_CENTER = #{CENTER}
AND DIB_MODEL like '%${MNAME}%'
AND DIB_IOTYPE = #{IOTYPE}
AND DIB_DOCNO like '%${EVENT}%' AND (DIB_IOTYPE = '1' AND DIB_RESN = '3' or DIB_IOTYPE = '2' AND DIB_RESN = '1')
) SUB ) MAIN WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT DOCNO, MODEL, IOTYPE, IODATE, QTY, RESNNAME, CCODE, MCI_LOGC, LOGCNAME, BONBUNAME, CENTERNAME, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP FROM (
SELECT DIL_DOCNO as DOCNO, DIL_MODEL as MODEL, '1' as IOTYPE, CONVERT(CHAR(10), DIL_INDate, 23) as IODATE, ISNULL(DIL_QTY,'0') as QTY, '' as RESNNAME, '0' as CCODE, MCI_LOGC, MCI_LOGCNAME as LOGCNAME, '-' as BONBUNAME, '-' as CENTERNAME, MMC_LGROUP, A.MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DT_IN_LOGC.CMN_UPD_DATE AS CMN_MAK_DATE FROM DT_IN_LOGC, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE A WHERE DIL_LOGC = MCI_LOGC AND MCI_Bonbu = ' ' AND MCI_Center = ' ' AND DIL_MODEL = MMC_CODE AND A.MCC_L_CODE = '200' AND A.MCC_M_CODE = '201' AND MMC_LGROUP = A.MCC_S_CODE
AND DIL_INDate <= #{EndDay}
AND DIL_INDate >= #{StartDay}
AND DIL_INDate between #{StartDay} AND #{EndDay}
AND DIL_LOGC = #{LOGC}
AND DIL_MODEL like '%${MNAME}%'
UNION ALL
SELECT DIB_DOCNO as DOCNO, DIB_MODEL as MODEL, DIB_IOTYPE as IOTYPE, CONVERT(CHAR(10), DIB_IODATE, 23) as IODATE, ISNULL(DIB_QTY,'0') as QTY, case when DIB_IOTYPE = '1' then B.MCC_S_NAME ELSE C.MCC_S_NAME end as RESNNAME, MCI_CODE as CCODE, MCI_LOGC, MCI_LOGCNAME as LOGCNAME, MCI_BonbuNAME as BONBUNAME, MCI_CenterName as CENTERNAME, MMC_LGROUP, A.MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DT_INOUT_BONBU.CMN_MAK_DATE AS CMN_MAK_DATE FROM DT_INOUT_BONBU LEFT OUTER JOIN MF_COMM_CODE B ON DIB_IOTYPE = '1' AND B.MCC_L_CODE = '100' AND B.MCC_M_CODE = '100' AND DIB_RESN = B.MCC_S_CODE LEFT OUTER JOIN MF_COMM_CODE C ON DIB_IOTYPE = '2' AND C.MCC_L_CODE = '100' AND C.MCC_M_CODE = '101' AND DIB_RESN = C.MCC_S_CODE, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE A WHERE DIB_CENTER = MCI_CODE AND DIB_MODEL = MMC_CODE AND A.MCC_L_CODE = '200' AND A.MCC_M_CODE = '201' AND MMC_LGROUP = A.MCC_S_CODE
AND DIB_IODATE <= #{EndDay}
AND DIB_IODATE >= #{StartDay}
AND DIB_IODATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_BONBU = #{BONBU}
AND MCI_CENTER = #{CENTER}
AND DIB_MODEL like '%${MNAME}%'
AND DIB_IOTYPE = #{IOTYPE}
AND DIB_DOCNO like '%${EVENT}%' AND (DIB_IOTYPE = '1' AND DIB_RESN = '3' or DIB_IOTYPE = '2' AND DIB_RESN = '1')
