SELECT rownum, MMC_CODE, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, MMJ_JEGO, PJEGO, (PJEGO - OUTJEGO) + INJEGO as NOWJEGO, INJEGO, OUTJEGO, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MMC_CODE) AS rownum, MMC_CODE, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, ISNULL(MMJ_JEGO, 0) as MMJ_JEGO, ISNULL(C.INQTY, 0) - ISNULL(D.OUTQTY, 0) as PJEGO, ISNULL(SUM(case WHEN DIB_IOTYPE = '1' THEN DIB_QTY else 0 end), 0) as INJEGO, ISNULL(SUM(case WHEN DIB_IOTYPE = '2' THEN DIB_QTY else 0 end), 0) as OUTJEGO, COUNT(*) OVER() AS totalCnt FROM MF_COMM_CODE, MF_MODEL_CODE left outer join DT_INOUT_BONBU on MMC_CODE = DIB_MODEL and DIB_CENTER = #{CENTER} AND DIB_IODATE between #{StartDay} and #{EndDay} left outer join MF_MODEL_JEGO on MMC_CODE = MMJ_MODEL AND MMJ_CENTER = #{CENTER} left outer join (SELECT DIB_MODEL, DIB_CENTER, SUM(DIB_QTY) AS INQTY FROM DT_INOUT_BONBU WHERE DIB_IOTYPE = '1' AND DIB_IODATE < dateadd(day,1,#{PDATE}) AND DIB_CENTER = #{CENTER} GROUP BY DIB_MODEL, DIB_CENTER ) C on C.DIB_MODEL = MMC_CODE left outer join (SELECT DIB_MODEL, DIB_CENTER, SUM(DIB_QTY) AS OUTQTY FROM DT_INOUT_BONBU WHERE DIB_IOTYPE = '2' AND DIB_IODATE < dateadd(day,1,#{PDATE}) AND DIB_CENTER = #{CENTER} GROUP BY DIB_MODEL, DIB_CENTER ) D on D.DIB_MODEL = MMC_CODE where MMC_LOGC = #{LOGC} AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE
AND MMC_LGROUP = #{LCODE}
AND MMC_MGROUP = #{MCODE}
AND MMC_SGROUP = #{SCODE}
AND MMC_MGROUP like '%${MCODENAME}%'
AND MMC_SGROUP like '%${SCODENAME}%'
AND MMC_CODE like '%${MNAME}%'
group by MMC_CODE, MMC_LGROUP, MCC_S_NAME, MMC_MGROUP, MMC_SGROUP, MMJ_JEGO, C.INQTY, D.OUTQTY ) MAIN
WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

select DOCNO, INTYPE, INBONBU, OUTBONBU, MODEL, OUTDATE, CFMsts, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP FROM ( select DBI_DOCNO as DOCNO, '0' AS INTYPE, B.MCI_BonbuNAME + ' ' + B.MCI_CenterName as INBONBU, A.MCI_LOGCNAME as OUTBONBU, DIL_MODEL as MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DIL_OUTDATE, 23) as OUTDATE, case when DIC_DOCNO is null then 'N' else 'Y' end as CFMsts FROM DT_IN_LOGC, MF_CENTER_INFO A, MF_CENTER_INFO B, MF_MODEL_CODE, MF_COMM_CODE, DT_BONBU_IN left outer join DT_IN_CFM on DBI_DOCNO = DIC_DOCNO WHERE DIL_DOCNO = DBI_TOPDOCNO AND DIL_LOGC = A.MCI_LOGC AND A.MCI_BONBU = ' ' AND A.MCI_CENTER = ' ' AND DIL_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND DBI_CENTER = B.MCI_CODE
AND DIL_OUTDATE <= #{EndDay}
AND DIL_OUTDATE >= #{StartDay}
AND DIL_OUTDATE between #{StartDay} AND #{EndDay}
AND B.MCI_LOGC = #{LOGC}
AND B.MCI_Bonbu = #{BONBU}
AND B.MCI_Center = #{CENTER}
UNION ALL select DBO_DOCNO AS DOCNO, '1' AS INTYPE, A.MCI_BonbuNAME + ' ' + A.MCI_CenterName as INBONBU, B.MCI_BonbuNAME + ' ' + B.MCI_CenterName as OUTBONBU, DBO_MODEL as MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, CONVERT(CHAR(10), DBM_DATE, 23) as OUTDATE, case when DIC_DOCNO is null then 'N' else 'Y' end as CFMsts FROM DT_BONBU_ORDER left outer join MF_CENTER_INFO A on DBO_CENTER = A.MCI_CODE left outer join DT_IN_CFM on DBO_DOCNO = DIC_DOCNO left outer join MF_MODEL_CODE on DBO_MODEL = MMC_CODE left outer join MF_COMM_CODE on MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE, DT_BONBU_MOVE left outer join MF_CENTER_INFO B on DBM_CENTER = B.MCI_CODE WHERE DBM_DOCNO = DBO_DOCNO AND DBM_Sts = 'Y'
AND DBM_DATE <= #{EndDay}
AND DBM_DATE >= #{StartDay}
AND DBM_DATE between #{StartDay} AND #{EndDay}
AND A.MCI_LOGC = #{LOGC}
AND A.MCI_Bonbu = #{BONBU}
AND A.MCI_Center = #{CENTER}
) SUB WHERE 1=1
AND CFMsts = #{CFMSTS}
ORDER BY CFMsts, OUTDATE DESC, DOCNO

   select DEO_DOCNO, DEO_CENTER, DEO_USER, CONVERT(CHAR(10), DEO_DATE, 23) as DEO_DATE, DEO_CMNT, MUT_UserName,
          MCI_LOGC, MCI_LOGCNAME, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName
	 FROM DT_EVENT_OUT, MF_USER_TBL, MF_CENTER_INFO
	WHERE DEO_USER = MUT_UserID
	  AND DEO_CENTER = MCI_CODE

	  AND DEO_DATE <= #{EndDay}


	  AND DEO_DATE >= #{StartDay}


	  AND DEO_DATE between #{StartDay} AND #{EndDay}


	  AND MCI_LOGC = #{LOGC}


	  AND MCI_Bonbu = #{BONBU}


	  AND MCI_Center = #{CENTER}


	  AND DEO_DOCNO like '%${EVENTNO}%'

    ORDER BY DEO_USER desc, DEO_CENTER

