AND DEM_DATE <= #{EndDay}
AND DEM_DATE >= #{StartDay}
AND DEM_DATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_Bonbu = #{BONBU}
AND MCI_Center = #{CENTER}
AND DEM_DOCNO like '%${ENAME}%'
AND DEM_STS = '1' AND DEM_RETEMPDATE > getdate()
AND DEM_STS = '1' AND DEM_RETEMPDATE < getdate()
AND DEM_STS = #{BISSTS}
) MAIN WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

