       CONVERT(CHAR(10), DEM_RETEMPDATE, 23) as DEM_RETEMPDATE, 
       CONVERT(CHAR(10), DEM_REDATE, 23) as DEM_REDATE, DEM_REUESR,
       DEM_STS, DEM_QTY, DEM_CMNT
  FROM DT_EVENT_MODEL
 WHERE DEM_DOCNO = #{DOCNO}
   AND DEM_CENTER = #{CENTER}
   AND DEM_MODEL = #{MODEL}

SELECT rownum, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DMJ_JEGO, MODEL_STS, CMNT, totalCnt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MCI_CODE, MMC_CODE) AS rownum, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DMJ_JEGO, MODEL_STS, CMNT, COUNT(*) OVER() AS totalCnt FROM (
SELECT MCI_CODE, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DMJ_JEGO, '' AS MODEL_STS, '' AS CMNT FROM DT_MODEL_JEGO, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE WHERE DMJ_CENTER = MCI_CODE AND DMJ_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND MMC_KINDS = '3' AND DMJ_JEGO > 0
AND MCI_LOGC = #{LOGC}
AND MCI_Bonbu = #{BONBU}
AND MCI_Center = #{CENTER}
AND MMC_CODE like '%${MNAME}%'
UNION ALL
SELECT MCI_CODE, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DMJ_JEGO, MODEL_STS, CMNT FROM ( SELECT MCI_CODE, MCI_LOGC, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DMJ_JEGO, DIB_RESN, DEM_STS, case when DIB_RESN = '1' AND DEM_STS = '3' then '' when DIB_RESN = '1' AND NOT DEM_STS = '3' then '' when DIB_RESN = '3' then '' else '' end AS MODEL_STS, DEM_CMNT AS CMNT, ROW_NUMBER() OVER (PARTITION BY MCI_Center, MMC_CODE ORDER BY DIB_IODATE DESC, DT_INOUT_BONBU.CMN_MAK_DATE DESC) AS RankNo FROM DT_MODEL_JEGO, MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE, DT_INOUT_BONBU LEFT OUTER JOIN DT_EVENT_MODEL ON DIB_MODEL = DEM_MODEL AND DIB_CENTER = DEM_CENTER AND DIB_DOCNO = DEM_DOCNO WHERE DMJ_CENTER = MCI_CODE AND DMJ_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND MMC_KINDS = '3' AND DMJ_JEGO = 0 AND DMJ_MODEL = DIB_MODEL AND DMJ_CENTER = DIB_CENTER AND DIB_IOTYPE = '2' ) T WHERE RankNo = 1 AND NOT DIB_RESN = '2'
AND MCI_LOGC = #{LOGC}
AND MCI_Bonbu = #{BONBU}
AND MCI_Center = #{CENTER}
AND MMC_CODE like '%${MNAME}%'
AND DIB_RESN = '1' AND NOT DEM_STS = '3'
AND DIB_RESN = '1' AND DEM_STS = '3'
AND DIB_RESN = '3'
) SUB ) MAIN WHERE rownum BETWEEN ((#{PAGE}-1)*#{PSIZE})+1 AND (#{PAGE}*#{PSIZE})

SELECT rownum, DEM_DOCNO, DEM_CENTER, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, DEM_MODEL, MMC_LGROUP, MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, DEM_QTY, DEM_DATE, DEM_USER, DEM_USERNAME, DEM_RETEMPDATE, DEM_REDATE, DEM_REUESR, DEM_REUESRNAME, DEM_STS, DEM_CMNT FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY MCI_Center, MMC_CODE ORDER BY DEM_DATE DESC, DT_EVENT_MODEL.CMN_MAK_DATE DESC) AS rownum, DEM_DOCNO, DEM_CENTER, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, DEM_MODEL, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP, ISNULL(DEM_QTY, 0) as DEM_QTY, CONVERT(CHAR(10), DEM_DATE, 23) as DEM_DATE, DEM_USER, A.MUT_UserName as DEM_USERNAME, CONVERT(CHAR(10), DEM_RETEMPDATE, 23) as DEM_RETEMPDATE, CONVERT(CHAR(10), DEM_REDATE, 23) as DEM_REDATE, DEM_REUESR, B.MUT_UserName as DEM_REUESRNAME, DEM_STS, DEM_CMNT FROM MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE, MF_USER_TBL A, DT_EVENT_MODEL left outer join MF_USER_TBL B on DEM_REUESR = B.MUT_UserID WHERE DEM_CENTER = MCI_CODE AND DEM_MODEL = MMC_CODE AND MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND DEM_USER = A.MUT_UserID AND MMC_KINDS = '3'
AND DEM_DATE <= #{EndDay}
AND DEM_DATE >= #{StartDay}
AND DEM_DATE between #{StartDay} AND #{EndDay}
AND MCI_LOGC = #{LOGC}
AND MCI_Bonbu = #{BONBU}
AND MCI_Center = #{CENTER}
AND DEM_DOCNO like '%${ENAME}%'
AND DEM_STS = #{BISSTS}
) MAIN WHERE rownum = 1

mapper.update:
	update DT_EVENT_MODEL 
      set DEM_STS = #{DEM_STS},
      	   DEM_CMNT = #{DEM_CMNT},
      	   DEM_REUESR = #{DEM_REUESR},
      	   DEM_REDATE = #{DEM_REDATE},
      	   CMN_UPD_DATE = getdate(),
      	   CMN_UPD_PROG = #{CMN_UPD_PROG},
      	   CMN_UPD_ID = #{CMN_UPD_ID}
    where DEM_DOCNO = #{DEM_DOCNO}
      AND DEM_CENTER = #{DEM_CENTER}
      AND DEM_MODEL = #{DEM_MODEL}

mapper.select:
SELECT '0' as sumtype, B.MCI_CODE, B.MCI_LOGC, B.MCI_LOGCNAME, B.MCI_Bonbu, B.MCI_BonbuNAME, B.MCI_Center, B.MCI_CenterName, B.MMC_CODE, B.MMC_LGROUP, B.MMC_LGROUPNAME, B.MMC_MGROUP, B.MMC_SGROUP, ISNULL(A.QTY, 0) AS pqty, ISNULL(A.PRICE, 0) AS pprice, ISNULL(C.DPI_QTY, 0) AS logcqty, ISNULL(C.PRICE, 0) AS logcprice, ISNULL(D.DPI_QTY, 0) AS moveinqty, ISNULL(D.PRICE, 0) AS moveinprice, ISNULL(E.DPI_QTY, 0) AS moveoutqty, ISNULL(E.PRICE, 0) AS moveoutprice, ISNULL(F.DPI_QTY, 0) AS otherqty, ISNULL(F.PRICE, 0) AS otherprice, ISNULL(G.DPI_QTY, 0) AS eventqty, ISNULL(G.PRICE, 0) AS eventprice, ISNULL(A.QTY, 0) + ISNULL(C.DPI_QTY, 0) + ISNULL(D.DPI_QTY, 0) + (ISNULL(E.DPI_QTY, 0)*(-1)) + (ISNULL(F.DPI_QTY, 0)*(-1)) + (ISNULL(G.DPI_QTY, 0)*(-1)) as nowqty, ISNULL(A.PRICE, 0) + ISNULL(C.PRICE, 0) + ISNULL(D.PRICE, 0) + (ISNULL(E.PRICE, 0)*(-1)) + (ISNULL(F.PRICE, 0)*(-1)) + (ISNULL(G.PRICE, 0)*(-1)) as nowprice FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MCI_CODE, MMC_CODE) AS rownum, MCI_CODE, MCI_LOGC, MCI_LOGCNAME, MCI_Bonbu, MCI_BonbuNAME, MCI_Center, MCI_CenterName, MMC_CODE, MMC_LGROUP, MCC_S_NAME as MMC_LGROUPNAME, MMC_MGROUP, MMC_SGROUP FROM MF_CENTER_INFO, MF_MODEL_CODE, MF_COMM_CODE WHERE MCC_L_CODE = '200' AND MCC_M_CODE = '201' AND MMC_LGROUP = MCC_S_CODE AND NOT MCI_Center = ' ' AND NOT MMC_KINDS = '3'
AND MCI_LOGC = #{LOGC}
AND MCI_Bonbu = #{BONBU}
AND MCI_Center = #{CENTER}
AND MMC_LGROUP = #{LGROUP}
AND MMC_KINDS = #{KINDS}
) B LEFT OUTER JOIN ( SELECT DIB_CENTER, DIB_MODEL, SUM(CASE WHEN DIB_IOTYPE = '1' THEN QTY ELSE QTY*(-1) END) AS QTY, SUM(CASE WHEN DIB_IOTYPE = '1' THEN PRICE ELSE PRICE*(-1) END) AS PRICE FROM ( SELECT ROW_NUMBER() OVER(ORDER BY DIB_CENTER, DIB_MODEL, DIB_IOTYPE) AS rownum, DIB_CENTER, DIB_MODEL, DIB_IOTYPE, DIB_IODATE, DPI_QTY AS QTY, DPI_PRICE AS UNIT, DPI_QTY * DPI_PRICE AS PRICE FROM DT_INOUT_BONBU, DT_PRICE_IN WHERE DIB_IOTYPE = '1' AND DIB_DOCNO = DPI_DOCNO AND DIB_CENTER = DPI_CENTER AND DIB_MODEL = DPI_MODEL AND DIB_IODATE < #{StartDay} UNION SELECT ROW_NUMBER() OVER(ORDER BY DIB_CENTER, DIB_MODEL, DIB_IOTYPE) AS rownum, DIB_CENTER, DIB_MODEL, DIB_IOTYPE, DIB_IODATE, DPO_QTY AS QTY, DPO_PRICE AS UNIT, DPO_QTY * DPO_PRICE AS PRICE FROM DT_INOUT_BONBU, DT_PRICE_OUT WHERE DIB_IOTYPE = '2' AND DIB_DOCNO = DPO_DOCNO AND DIB_CENTER = DPO_CENTER AND DIB_MODEL = DPO_MODEL AND DIB_IODATE < #{StartDay} ) A GROUP BY DIB_CENTER, DIB_MODEL ) A ON A.DIB_CENTER = B.MCI_CODE AND A.DIB_MODEL = B.MMC_CODE LEFT OUTER JOIN ( SELECT DPI_CENTER, DPI_MODEL, SUM(DPI_QTY) AS DPI_QTY, SUM(DPI_QTY * DPI_PRICE) AS PRICE FROM DT_BONBU_IN, DT_PRICE_IN, DT_IN_LOGC WHERE DBI_BisSts = 'Y' AND DBI_CFMDATE BETWEEN #{StartDay} AND #{EndDay} AND DBI_DOCNO = DPI_DOCNO AND DBI_TOPDOCNO = DIL_DOCNO AND DIL_MODEL = DPI_MODEL AND DBI_CENTER = DPI_CENTER GROUP BY DPI_CENTER, DPI_MODEL ) C ON C.DPI_CENTER = B.MCI_CODE AND C.DPI_MODEL = B.MMC_CODE LEFT OUTER JOIN ( SELECT DPI_CENTER, DPI_MODEL, SUM(DPI_QTY) AS DPI_QTY, SUM(DPI_QTY * DPI_PRICE) AS PRICE FROM DT_BONBU_ORDER, DT_BONBU_MOVE, DT_PRICE_IN WHERE DBO_BisSts = 'Y' AND DBO_CFMDATE BETWEEN #{StartDay} AND #{EndDay} AND DBO_DOCNO = DPI_DOCNO AND DBO_DOCNO = DBM_DOCNO AND DBO_MODEL = DPI_MODEL AND DBO_CENTER = DPI_CENTER GROUP BY DPI_CENTER, DPI_MODEL ) D ON D.DPI_CENTER = B.MCI_CODE AND D.DPI_MODEL = B.MMC_CODE LEFT OUTER JOIN ( SELECT DPO_CENTER, DPO_MODEL, SUM(DPO_QTY) AS DPI_QTY, SUM(DPO_QTY * DPO_PRICE) AS PRICE FROM DT_BONBU_ORDER, DT_BONBU_MOVE, DT_PRICE_OUT WHERE DBM_Sts = 'Y' AND DBM_DATE BETWEEN #{StartDay} AND #{EndDay} AND DBO_DOCNO = DPO_DOCNO AND DBO_DOCNO = DBM_DOCNO AND DBO_MODEL = DPO_MODEL AND DBM_CENTER = DPO_CENTER GROUP BY DPO_CENTER, DPO_MODEL ) E ON E.DPO_CENTER = B.MCI_CODE AND E.DPO_MODEL = B.MMC_CODE LEFT OUTER JOIN ( SELECT DPO_CENTER, DPO_MODEL, SUM(DPO_QTY) AS DPI_QTY, SUM(DPO_QTY * DPO_PRICE) AS PRICE FROM DT_Other_OUT, DT_PRICE_OUT WHERE DOO_DATE BETWEEN #{StartDay} AND #{EndDay} AND DOO_DOCNO = DPO_DOCNO AND DOO_MODEL = DPO_MODEL AND DOO_CENTER = DPO_CENTER GROUP BY DPO_CENTER, DPO_MODEL ) F ON F.DPO_CENTER = B.MCI_CODE AND F.DPO_MODEL = B.MMC_CODE LEFT OUTER JOIN ( SELECT DPO_CENTER, DPO_MODEL, SUM(DPO_QTY) AS DPI_QTY, SUM(DPO_QTY * DPO_PRICE) AS PRICE FROM DT_EVENT_MODEL, DT_PRICE_OUT WHERE DEM_DATE BETWEEN #{StartDay} AND #{EndDay} AND DEM_DOCNO = DPO_DOCNO AND DEM_MODEL = DPO_MODEL AND DEM_CENTER = DPO_CENTER GROUP BY DPO_CENTER, DPO_MODEL ) G ON G.DPO_CENTER = B.MCI_CODE AND G.DPO_MODEL = B.MMC_CODE ORDER BY B.MCI_CODE, B.MMC_CODE
